<template>
  <div ref="game"></div>
</template>

<script>
import Phaser from 'phaser';

export default {
  mounted() {
    const config = {
      type: Phaser.AUTO,
      width: 800,
      height: 600,
      scene: {
        preload: this.preload,
        create: this.create,
        update: this.update
      }
    };

    this.game = new Phaser.Game(config);
  },
  methods: {
    preload() {
      // Aquí puedes cargar los recursos del juego como imágenes, sonidos, etc.
    },
    create() {
      // Aquí puedes configurar los objetos y la lógica inicial del juego
    },
    update() {
      // Aquí se ejecutará el bucle del juego (actualización de objetos, detección de colisiones, etc.)
    }
  },
  beforeUnmount() {
    this.game.destroy(true);
  }
};
</script>
