import AppLayout from '@/AppLayout.vue';


<template>
  <div ref="game"></div>
</template>

<script>
import Phaser from 'phaser';

let cursors;

export default {
  mounted() {
    const config = {
      type: Phaser.AUTO,
      width: 800,
      height: 600,
      scene: {
        preload: this.preload,
        create: this.create,
        update: this.update
      }
    };

    this.game = new Phaser.Game(config);
  },
  methods: {
    preload() {
      // Aquí puedes cargar los recursos del juego como imágenes, sonidos, etc.
        this.scene.load.image('paddle', 'src/imgs/pala.png');
        this.scene.load.image('ball', 'src/imgs/pelota.png');
    },
    create() {
      // Configuracion/creacion objetos y la lógica inicial del juego
      this.load.image('paddle', '../imgs/pala.png');
      this.load.image('ball', '../imgs/pelota.png');
      // Agregar las palas
        this.paddleLeft = this.physics.add.sprite(50, this.cameras.main.centerY, 'paddle');
        this.paddleRight = this.physics.add.sprite(750, this.cameras.main.centerY, 'paddle');
      // Agregar la pelota
        this.ball = this.physics.add.sprite(this.cameras.main.centerX, this.cameras.main.centerY, 'ball');
      cursors = this.input.keyboard.createCursorKeys();
    },
    update() {
        //logica de movimiento y colisiones
        if (cursors.up.isDown) {
          this.paddleRight.setVelocityY(-160);
        } else if (cursors.down.isDown) {
          this.paddleRight.setVelocityY(160);
        } else {
          this.paddleRight.setVelocityY(0);
      }
    }
  },
  beforeUnmount() {
    this.game.destroy(true);
  }
};
</script>
